import React, { useContext } from 'react';
import { AppContext } from '../Components/utils/global.context';
import Card from '../Components/Card';

const Favs = () => {
  const { state } = useContext(AppContext);

  // Obtener los dentistas destacados del localStorage
  const favorites = localStorage.getItem('favorites')
    ? JSON.parse(localStorage.getItem('favorites'))
    : [];

  const themeClass = state.theme === 'dark' ? 'dark' : 'light';

  return (
    <div className={themeClass}>
      <h1>Dentists Favs</h1>
      <div className="card-grid">
        {favorites.length > 0 ? (
          favorites.map((favorite) => (
            <Card
              key={favorite.id} // Asegúrate de tener un identificador único para cada Card
              id={favorite.id}
              name={favorite.name}
              username={favorite.username}
            />
          ))
        ) : (
          <p>No hay dentistas destacados</p>
        )}
      </div>
    </div>
  );
};

export default Favs;
