import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { AppContext } from "./utils/global.context";

const Card = ({ name, username, id }) => {
  const { state, dispatch } = useContext(AppContext);

  const addFav = () => {
    // Aquí iría la lógica para agregar la Card al localStorage
    dispatch({ type: "ADD_TO_FAVORITES", payload: { id, name, username } });
  };

  const themeClass = state.theme === "dark" ? "dark" : "light";

  return (
    <div className={`card ${themeClass}`}>
      {/* Mostrar name, username e id */}
      <h3>{name}</h3>
      <p>{username}</p>
      <p>ID: {id}</p>

      {/* Enlace a la página de detalle */}
      <Link to={`/dentist/${id}`} className="detailLink">
        Ver detalles
      </Link>

      {/* Imagen de estrella con evento onClick para agregar a favoritos */}
      <img
        src="estrella1.jpg" // Ajusta la ruta según la estructura de tu proyecto
        alt="Star"
        className="favStar"
        onClick={addFav}
      />
    </div>
  );
};

export default Card;
