import React, { createContext, useReducer, useMemo } from "react";

// Definir las acciones que pueden modificar el estado global
const actionTypes = {
  TOGGLE_THEME: "TOGGLE_THEME",
  SET_API_DATA: "SET_API_DATA",
  ADD_TO_FAVORITES: "ADD_TO_FAVORITES",
};

// Función reductora para manejar las acciones y actualizar el estado global
const appReducer = (state, action) => {
  switch (action.type) {
    case actionTypes.TOGGLE_THEME:
      return { ...state, theme: state.theme === "light" ? "dark" : "light" };
    case actionTypes.SET_API_DATA:
      return { ...state, data: { apiData: action.payload } }; // Cambiado aquí
    // Puedes agregar más casos para otras acciones según sea necesario
    default:
      return state;
  }
};

export const initialState = {
  theme: "light",
  data: { apiData: [] }, // Cambiado aquí
  favorites: [],
};

export const AppContext = createContext();

export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(appReducer, initialState);

  // Utilizar useMemo para memorizar el contexto y evitar renders innecesarios
  const contextValue = useMemo(() => ({ state, dispatch }), [state, dispatch]);

  return (
    <AppContext.Provider value={contextValue}>
      {children}
    </AppContext.Provider>
  );
};
