import React, { useEffect, useState, useContext } from 'react';
import { useParams, Link } from 'react-router-dom';
import { AppContext } from '../Components/utils/global.context';

const Detail = () => {
  const { state, dispatch } = useContext(AppContext);
  const { id } = useParams();
  const [userDetails, setUserDetails] = useState(null);

  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        // Realiza un fetch a la API para obtener los detalles del usuario con el ID proporcionado
        const response = await fetch(`https://jsonplaceholder.typicode.com/users/${id}`);
        const data = await response.json();
        setUserDetails(data);
      } catch (error) {
        console.error('Error fetching user details:', error);
      }
    };

    fetchUserDetails();
  }, [id]);

  const themeClass = state.theme === 'dark' ? 'dark' : 'light';

  const handleAddToFavorites = () => {
    // Despacha la acción para agregar a favoritos
    dispatch({
      type: 'ADD_TO_FAVORITES',
      payload: {
        id: userDetails.id,
        name: userDetails.name,
        username: userDetails.username,
      },
    });
  };

  return (
    <div className={themeClass}>
      {userDetails ? (
        <>
          <h1>Detail Dentist id {id}</h1>
          <p>Name: {userDetails.name}</p>
          <p>Email: {userDetails.email}</p>
          <p>Phone: {userDetails.phone}</p>
          <p>Website: {userDetails.website}</p>
          {/* Agrega un botón para agregar a favoritos */}
          <button onClick={handleAddToFavorites}>Add to Favorites</button>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Detail;
